      isShow.value = true
    })
    const emitClick = () => emit('emitClick')
    const successAnswer = (appliance, event) => {
      tryClicked.value = false
      emit('successAnswer', appliance)
      tryClicked.value = false
      event.stopPropagation()
    }
    const tryClick = (e) => {
      tryClicked.value = true
    }
    return {
      isShow,
      emitClick,
      successAnswer,
      tryClicked,
      tryClick
    }
  }
}
</script>