<script>
import { ref, onMounted } from 'vue';
export default {
  name: 'home',
  props: {
    lightingHome: {
      type: String,
      default: ''
    }
  },
  setup(props, ctx) {
    const { emit } = ctx
    const isShow = ref(false)
    const tryClicked = ref(false)
    const tryClickedFirst = ref(false)
    onMounted(() => {
      isShow.value = true
    })
    const emitClick = () => emit('emitClick')
    const successAnswer = (appliance, event) => {
      tryClicked.value = false
      tryClickedFirst.value = false
      emit('successAnswer', appliance)
      tryClicked.value = false
      event.stopPropagation()
    }
    const tryClick = () => {
      // tryClicked.value = true
      if (tryClicked.value) return
      if (!tryClickedFirst.value) {
        if (props.lightingHome === 'termometr') {
          emit('addMessage', ' ,      .')
        }
        if (props.lightingHome === 'girometr') {
          emit('addMessage', ' ,   .')
        }
        if (props.lightingHome === 'barometr') {
          emit('addMessage', ' ,   .')
        }
        tryClickedFirst.value = true
      }
      else {
        tryClicked.value = true
        if (props.lightingHome === 'termometr') {
          emit('addMessage', ' ,      .')
        }
        if (props.lightingHome === 'girometr') {
          emit('addMessage', ' ,     .')
        }
        if (props.lightingHome === 'barometr') {
          emit('addMessage', ' ,     .')
        }
      }
    }
    return {
      isShow,
      emitClick,
      successAnswer,
      tryClicked,
      tryClick,
      tryClickedFirst
    }
  }
}
</script>